package consolizer

import (
	"github.com/stretchr/testify/assert"
	"testing"
	"time"
)

func TestTooltipThinLine(test *testing.T) {
	layer2, _, _, styleEntry := CommonTestSetup()
	layer2.AddTooltip("This is a tooltip!", styleEntry, 0, 0, 20, 5, 3, 3, 25, 1, false, false, 0)
	SetMouseStatus(0, 0, 0, "")
	time.Sleep(1 * time.Second)
	UpdateDisplay(false)
	UpdatePeriodicEvents()
	time.Sleep(1 * time.Second)
	UpdateDisplay(false)
	UpdatePeriodicEvents()
	layerEntry := commonResource.screenLayer
	obtainedValue := layerEntry.GetBasicAnsiStringAsBase64()
	expectedValue := ""
	assert.Equalf(test, expectedValue, obtainedValue, "The updated screen does not match the master original!")
}

