func TestTooltipWithDelay(test *testing.T) {
	layer2, _, _, styleEntry := CommonTestSetup()
	layer2.AddTooltip("This is a tooltip and this works great!", styleEntry, 0, 0, 20, 5, 3, 3, 15, 10, false, true, 2000)
	SetMouseStatus(0, 0, 0, "")
	UpdateDisplay(false)
	UpdatePeriodicEvents()
	time.Sleep(1 * time.Second)
	UpdateDisplay(false)
	UpdatePeriodicEvents()
	layerEntry := commonResource.screenLayer
	obtainedValue := layerEntry.GetBasicAnsiStringAsBase64()
	expectedValue := ""
	assert.Equalf(test, expectedValue, obtainedValue, "The updated first screen does not match the master original!")
	time.Sleep(2 * time.Second)
	UpdateDisplay(false)
	UpdatePeriodicEvents()
	layerEntry = commonResource.screenLayer
	obtainedValue = layerEntry.GetBasicAnsiStringAsBase64()
	expectedValue = ""
	assert.Equalf(test, expectedValue, obtainedValue, "The updated second screen does not match the master original!")
}
